INSERT INTO `wp_posts` VALUES (5,1,'2020-09-23 14:40:14','2020-09-23 04:40:14','<!-- wp:columns {\"className\":\"w-75 my-5 skew-bottom-spacing\"} -->\n<div class=\"wp-block-columns w-75 my-5 skew-bottom-spacing\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph -->\n<p>VeriSkills has identified 14 core capabilities as the most important ‘soft’ or non-technical skills and knowledge required in the future workforce.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>These capabilities are essential requirements for future work and employability, irrespective of the profession or the individual’s job, work level, or occupation.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph -->\n<p>In contrast with technical skills, human capabilities are the underlying knowledge, skills, and personal attributes or experiences required to perform in a role today, and to confirm a person’s potential to rapidly assume emerging future roles.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>They are broad, generic, and transferrable, and this distinguishes them from technical and vocational competencies, which tend to be specific to units of knowledge and skill tied to demonstrated tasks.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":8,\"mediaLink\":\"https://veriskills.local/about-human-capabilities/humancapabilitiesheadicon/\",\"mediaType\":\"image\",\"className\":\"hero bg-1 skew-bottom\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile hero bg-1 skew-bottom\"><figure class=\"wp-block-media-text__media\"><img src=\"https://veriskills.local/wp-content/uploads/2020/09/HumanCapabilitiesHeadIcon.png\" alt=\"\" class=\"wp-image-8\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading {\"level\":4,\"className\":\"fg-3\"} -->\n<h4 class=\"fg-3\">Each capability falls into one of three domains:</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><li>personal</li><li>action, and</li><li>thinking.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4,\"className\":\"fg-3\"} -->\n<h4 class=\"fg-3\">Within those, each capability is measured at one of five levels:</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><li>foundation</li><li>intermediate</li><li>proficient</li><li>advanced and</li><li>expert.</li></ul>\n<!-- /wp:list --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:columns {\"className\":\"full-width no-spacing\"} -->\n<div class=\"wp-block-columns full-width no-spacing\"><!-- wp:column {\"className\":\"bg-3 lightenx3 skew-bottom-spacing\"} -->\n<div class=\"wp-block-column bg-3 lightenx3 skew-bottom-spacing\"><!-- wp:media-text {\"mediaId\":11,\"mediaLink\":\"https://veriskills.local/about-human-capabilities/humancapabilitiesicons_thinking/\",\"mediaType\":\"image\",\"className\":\"has-media-on-the-top\"} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile has-media-on-the-top\"><figure class=\"wp-block-media-text__media\"><img src=\"https://veriskills.local/wp-content/uploads/2020/09/HumanCapabilitiesIcons_Thinking.png\" alt=\"\" class=\"wp-image-11\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading -->\n<h2>Thinking</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Critical Thinking</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Creativity</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Adaptive Mindset</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Innovative Thinking</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"className\":\"bg-3 lightenx2 skew-bottom-spacing\"} -->\n<div class=\"wp-block-column bg-3 lightenx2 skew-bottom-spacing\"><!-- wp:media-text {\"mediaId\":10,\"mediaLink\":\"https://veriskills.local/about-human-capabilities/humancapabilitiesicons_personal/\",\"mediaType\":\"image\",\"className\":\"has-media-on-the-top\"} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile has-media-on-the-top\"><figure class=\"wp-block-media-text__media\"><img src=\"https://veriskills.local/wp-content/uploads/2020/09/HumanCapabilitiesIcons_Personal.png\" alt=\"\" class=\"wp-image-10\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading -->\n<h2>Personal</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Lifelong Learning</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Initiative &amp; Drive</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cultural Awareness</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Ethics</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Empathy</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"className\":\"bg-3 lighten skew-bottom-spacing\"} -->\n<div class=\"wp-block-column bg-3 lighten skew-bottom-spacing\"><!-- wp:media-text {\"mediaId\":9,\"mediaLink\":\"https://veriskills.local/about-human-capabilities/humancapabilitiesicons_action/\",\"mediaType\":\"image\",\"className\":\"has-media-on-the-top\"} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile has-media-on-the-top\"><figure class=\"wp-block-media-text__media\"><img src=\"https://veriskills.local/wp-content/uploads/2020/09/HumanCapabilitiesIcons_Action.png\" alt=\"\" class=\"wp-image-9\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading -->\n<h2>Action</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Communication</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Collaboration</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Problem Solving</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Digital Acumen</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Customer Focus</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:heading {\"align\":\"center\",\"level\":3,\"className\":\"bg-3 hero\"} -->\n<h3 class=\"has-text-align-center bg-3 hero\"><a href=\"#\">Click here to access the complete Human Capability Framework</a></h3>\n<!-- /wp:heading -->','About Human Capabilities','','publish','closed','closed','','about-human-capabilities','','','2020-09-25 16:23:33','2020-09-25 06:23:33','',0,'https://veriskills.local/?page_id=5',0,'page','',0);
