INSERT INTO `wp_posts` VALUES (152,1,'2020-09-25 16:51:24','2020-09-25 06:51:24','<!-- wp:media-text {\"mediaId\":85,\"mediaLink\":\"https://veriskills.local/?attachment_id=85\",\"mediaType\":\"image\",\"className\":\"home-hero\"} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile home-hero\"><figure class=\"wp-block-media-text__media\"><img src=\"https://veriskills.local/wp-content/uploads/2020/09/laura-d-vargas-V68OBnf41DI-unsplash-768x1024.jpg\" alt=\"\" class=\"wp-image-85\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:group {\"className\":\"home-hero-content-top bg-4\"} -->\n<div class=\"wp-block-group home-hero-content-top bg-4\"><div class=\"wp-block-group__inner-container\"><!-- wp:heading {\"level\":1} -->\n<h1>We all need to prepare for a future of unimagined possibilities</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>A future driven by technologies that we have not dreamt of yet. Dominated by jobs that do not yet exist. Where mass collaboration is the norm.</h4>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Are you ready?</h3>\n<!-- /wp:heading -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"className\":\"btn\"} -->\n<div class=\"wp-block-button btn\"><a class=\"wp-block-button__link\">About VeriSkills</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"></div></div>\n<!-- /wp:group -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"home-hero-content-bottom\"} -->\n<div class=\"wp-block-group home-hero-content-bottom\"><div class=\"wp-block-group__inner-container\"><!-- wp:columns {\"className\":\"w-100\"} -->\n<div class=\"wp-block-columns w-100\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"level\":4} -->\n<h4>Add value to your training course</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Verify courses and programs against the human capabilities framework to ensure outcomes are measurable and transferable.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"level\":4} -->\n<h4>Be part of the future</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>The way qualifications are issued and recognised is changing. This will inform candidate selection, employer attractiveness and quality of training programs. Don\'t be left behind.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','Home Hero','','inherit','closed','closed','','84-revision-v1','','','2020-09-25 16:51:24','2020-09-25 06:51:24','',84,'https://veriskills.local/84-revision-v1/',0,'revision','',0);
