><!-- wp:button {\"borderRadius\":0,\"className\":\"btn\"} -->\n<div class=\"wp-block-button btn\"><a class=\"wp-block-button__link no-border-radius\">Learn More</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"align\":\"center\",\"level\":4} -->\n<h4 class=\"has-text-align-center\">What is digital badging?</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Communicate your capabilities with employers and colleagues</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"borderRadius\":0,\"className\":\"btn\"} -->\n<div class=\"wp-block-button btn\"><a class=\"wp-block-button__link no-border-radius\">Learn More</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:image {\"id\":19,\"width\":1024,\"height\":683,\"sizeSlug\":\"large\",\"className\":\"w-100\"} -->\n<figure class=\"wp-block-image size-large is-resized w-100\"><img src=\"https://veriskills.local/wp-content/uploads/2020/09/marvin-meyer-SYTO3xs06fU-unsplash-1024x683.jpg\" alt=\"\" class=\"wp-image-19\" width=\"1024\" height=\"683\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','Home','','inherit','closed','closed','','68-revision-v1','','','2020-09-25 13:29:51','2020-09-25 03:29:51','',68,'https://veriskills.local/68-revision-v1/',0,'revision','',0);
