\n<!-- /wp:media-text -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:heading {\"align\":\"center\",\"level\":3,\"className\":\"bg-3 hero\"} -->\n<h3 class=\"has-text-align-center bg-3 hero\"><a href=\"#\">Click here to access the complete Human Capability Framework</a></h3>\n<!-- /wp:heading -->','About Human Capabilities','','inherit','closed','closed','','5-revision-v1','','','2020-09-27 13:40:31','2020-09-27 03:40:31','',5,'https://veriskills.local/5-revision-v1/',0,'revision','',0);
