INSERT INTO `wp_posts` VALUES (189,1,'2020-09-27 13:50:07','2020-09-27 03:50:07','<!-- wp:paragraph -->\n<p>QTAC has extensive relationships with education providers. Entry into a course is set by an in individual provider and entry requirements will vary between courses. We expect providers will use VeriSkills in assisting with entry.</p>\n<!-- /wp:paragraph -->','Will my credentials get me access into a course of further study?','','publish','closed','closed','','will-my-credentials-get-me-access-into-a-course-of-further-study','','','2020-09-28 10:47:30','2020-09-28 00:47:30','',0,'https://veriskills.local/?post_type=faq&#038;p=189',60,'faq','',0);
