INSERT INTO `wp_posts` VALUES (191,1,'2020-09-27 13:50:23','2020-09-27 03:50:23','<!-- wp:paragraph -->\n<p>The technology used on the Acclaim Platform is based on the Open Badge Standards maintained by IMS Global. This enables you to manage, share and verify your competencies digitally.</p>\n<!-- /wp:paragraph -->','How can I share the capabilities that I have achieved?','','publish','closed','closed','','how-can-i-share-the-capabilities-that-i-have-achieved','','','2020-09-28 10:47:45','2020-09-28 00:47:45','',0,'https://veriskills.local/?post_type=faq&#038;p=191',70,'faq','',0);
