ph -->\n<p>Digital Acumen</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Customer Focus</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:heading {\"align\":\"center\",\"level\":3,\"className\":\"bg-3 hero no-link-underline\"} -->\n<h3 class=\"has-text-align-center bg-3 hero no-link-underline\"><a href=\"#\"><strong>Click here to access the complete Human Capability Framework</strong></a></h3>\n<!-- /wp:heading -->','About Human Capabilities','','inherit','closed','closed','','5-revision-v1','','','2020-10-01 13:16:13','2020-10-01 03:16:13','',5,'https://veriskills.local/5-revision-v1/',0,'revision','',0);
