INSERT INTO `wp_posts` VALUES (288,2,'2020-10-01 16:20:41','2020-10-01 06:20:41','<!-- wp:media-text {\"mediaId\":285,\"mediaLink\":\"https://veriskills.local/pexels-fauxels-3184162/\",\"mediaType\":\"image\",\"className\":\"home-hero\"} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile home-hero\"><figure class=\"wp-block-media-text__media\"><img src=\"https://veriskills.local/wp-content/uploads/2020/10/pexels-fauxels-3184162-683x1024.jpg\" alt=\"\" class=\"wp-image-285\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:group {\"className\":\"home-hero-content-top bg-4\"} -->\n<div class=\"wp-block-group home-hero-content-top bg-4\"><div class=\"wp-block-group__inner-container\"><!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:heading {\"level\":1} -->\n<h1>We all need to prepare for a future of unimagined possibilities</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>A future driven by technologies that we have not dreamt of yet. Dominated by jobs that do not yet exist. Where mass collaboration is the norm.</h4>\n<!-- /wp:heading --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:heading {\"level\":3} -->\n<h3>Are you ready?</h3>\n<!-- /wp:heading -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link\" href=\"https://veriskills.local/about-veriskills/\">About VeriSkills</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"home-hero-content-bottom\"} -->\n<div class=\"wp-block-group home-hero-content-bottom\"><div class=\"wp-block-group__inner-container\"><!-- wp:columns {\"className\":\"w-100\"} -->\n<div class=\"wp-block-columns w-100\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"level\":4} -->\n<h4>Add value to your training course</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Verify courses and programs against the human capabilities framework to ensure outcomes are measurable and transferable.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"level\":4} -->\n<h4>Be part of the future</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>The way qualifications are issued and recognised is changing. This will inform candidate selection, employer attractiveness and quality of training programs. Don\'t be left behind.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','Home Hero','','inherit','closed','closed','','84-revision-v1','','','2020-10-01 16:20:41','2020-10-01 06:20:41','',84,'https://veriskills.local/84-revision-v1/',0,'revision','',0);
